    private void jComboBox2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox2ActionPerformed

        String driverage = jComboBox2.getSelectedItem().toString();
        
        if (driverage.equals("17-21")){
            initial = initial + (initial*0.15);
         quote += initial;}
         if (driverage.equals("22-30")){
             initial = initial + (initial*0.1);
         quote += initial;}
         if (driverage.equals("31-80")){
             quote += initial;
         }
         if (driverage.equals("80+")){
             initial = initial + (initial*0.22);
         quote += initial;}
        
        // TODO add your handling code here:
    }//GEN-LAST:event_jComboBox2ActionPerformed

    private void jComboBox3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox3ActionPerformed

        String ncb = jComboBox3.getSelectedItem().toString();
       switch (ncb) {
           case "5+ years":
               quote = quote - (quote*(0.25));
               break;
           case "4 years":
               quote = quote -(quote*(0.2));
               break;
           case "3 years":
               quote = quote -(quote*(0.15));
               break;
           case "2 years":
               quote = quote - (quote*(0.1));
               break;
           case "1 year":
               quote = quote - (quote*(0.05));
               break;
       }
        // TODO add your handling code here:
    }//GEN-LAST:event_jComboBox3ActionPerformed

